import styled from 'styled-components';
import {BrowserRouter as Router, Link, Route} from 'react-router-dom';
import {Button} from 'reactstrap';


export default function Landing (props) {


    return (
        // Render the Pizza component 
        <div> 
            <StyledBanner>
                <div className='banner-wrapper'> 
                    <img className='banner-image' src= 'https://relaxedpowerful.s3.us-east-2.amazonaws.com/landing-banner.jpg' alt='banner'/> 
                </div>
                <div className='headline-wrapper'>
                    <h1>
                        <span>
                            Co-Make
                        </span>
                    </h1>
                    <h3> 
                        <span>
                            The best place for community cooperation
                        </span>
                    </h3>
                </div>
                <div className='buttons-wrapper'> 
                   {/* Big buttons for desktop */}

                    <Button color='primary' className='big'>
                        Sign up 
                    </Button> {'  '}
                    <Button color='primary' className='big'>
                        Existing Member? Login
                    </Button> {'  '}
                    <Button color='success' className='big'>
                        What members are saying
                    </Button>

                {/* Small buttons for smaller screens*/}
                    <Button size="sm" color='primary' className='small'>
                        Sign up 
                    </Button> {'  '}
                    <Button size="sm" color='primary' className='small'>
                        Existing Member? Login
                    </Button> {'  '}
                    <Button size="sm" color='success' className='small'>
                        What members are saying
                    </Button>
                </div>
            </StyledBanner>            
        </div>

    )
};

const StyledBanner =styled.div `
    /* border: green 2px dashed; */
    position: relative;
    margin-top: 20px;    
    display: flex;
    flex-flow: column wrap;
    justify-content: center;
    align-items: center;

    ////MEDIA QUERIES
    @media (max-width: 500px){
    border: 5px red dashed;
    flex-flow: column wrap;
    max-width: 100%;
        .buttons-wrapper {
            flex-flow: column;
            border: red 2px solid;
            position: relative;
            bottom: 1%;
        }

        .big {
            display: none;
            color: red;
        }
        Button {

        }
    }

    .banner-wrapper{
        /* border: 1px red solid; */
        display: flex;
        flex-flow: column wrap;
        justify-content: center;
        align-items: center;
        /* max-width: 90%; */
        /* max-height: 20%; */
    }
    .banner-image {
        width: 100%;
        height: auto;
    }
    .headline-wrapper {
        /* border: white  2px solid; */
        display: flex;
        flex-flow: column;
        position: absolute; 
        top: 10%; 
        align-items: baseline;
        width: 100%; 
    }
    .buttons-wrapper {
        /* border: red dashed 9px; */
        position: absolute;
        display: flex;
        /* min-width: 40%; */
        justify-content: space-between;
    }

    span { 
        color: white; 
        font: bold Helvetica, Sans-Serif; 
        letter-spacing: -1px;  
        background: rgb(0, 0, 0); /* fallback color */
        background: rgba(0, 0, 0, 0.7);
   }
`
