import React, {useEffect, useState} from 'react';

import styled from 'styled-components';
import { Button } from 'reactstrap'; 
import {useForm} from 'react-hook-form';
import axios from 'axios';


export default function SubmitNewPost () {
    ////STATES
    //Form data state to store the data before sending it to the API
    const [postFormData, setPostFormData] = useState({});


    //Submit event handler
    const onSubmit = function (data, event) {
        console.log(data);

        //Update form state
        setPostFormData(data);

        //Clear the form
        event.target.reset();

        //Redirect the user to the posts page showing all posts

    };

    console.log(postFormData);
    ////FORM VALIDATION
    const { register, handleSubmit, errors } = useForm();

    ////SIDE EFFECTS
    //Send the data to the API
    useEffect((postFormData)=> {
        axios
            .put('https://bw-comake.herokuapp.com/posts', postFormData)
            .then(res => {
                console.log(res);
            })
    });

    return (
        <StyledFormWrapper>
        <div className='new-post-form'>
            <form onSubmit={handleSubmit(onSubmit)}>
                <input
                    ref={register({required: 'Please choose a title for your post', maxLength:{value: 25, message: 'Titles cannot be longer than 25 characters'}})}
                    name='title'
                    type='text'

                    >
                        {errors.title && errors.title.message}
                </input>
                <Button 
                type='submit'
                color='primary'>
                    Submit
                </Button>
            </form>
        </div>
        </StyledFormWrapper>
    )
}

const StyledFormWrapper = styled.div `
    border: green 5px dashed;

    display: flex;
    flex-flow: column wrap;
    justify-content: center;
    align-items: center;
`
