import React from 'react';
import {useState, useEffect} from 'react';
import {useForm} from 'react-hook-form';
import styled from 'styled-components';
import { Button, Form, Input, FormFeedback, FormGroup } from 'reactstrap'; 
import axios from 'axios';


export default function Login () {

    ////FORM STATE
    // const [initialFormValues, setInitialFormValues] = useState({
    //     email: '',
    //     password: '',
    //     }
    // )
    // const [formData, setFormData] = useState(initialFormValues);

    ////FORM VALIDATION
    //Extract the useForm hook methods
    const {
        register,
        errors,
        handleSubmit,
        reset,
        formState: { isSubmitSuccessful }
      } = useForm({ defaultValues: { something: "anything" } });

    const [submittedData, setSubmittedData] = useState({});

    const onSubmit = (data) => {
        setSubmittedData(data);
    };

    useEffect(() => {
        if (isSubmitSuccessful) {
        reset({ ...submittedData });
        }
    }, [isSubmitSuccessful, submittedData, reset]);



    return (

        <StyledLogin>
            <Form onSubmit={handleSubmit(onSubmit)}>
                    <FormGroup>
                    <Input type="email" name="email" id="email" placeholder="Email" innerRef={register({required: true} )}/>
                    {errors.email && "Email is required"}
                    </FormGroup>
                    <FormGroup innerRef={register}>
                    <Input type="password" name="password" id="password" placeholder="Password" innerRef={register({required: 'Please choose a password', minLength: {value: 8, message: 'Min length is 8 characters'} })}/>
                    {errors.password && <p> {errors.password.message} </p>} 
                    </FormGroup >
                    <FormGroup innerRef={register}>
                    <Button color='primary' type='submit'> Submit </Button>
                    </FormGroup>
            </Form>
        </StyledLogin>

    );
};

const StyledLogin = styled.div`
display: flex;
padding-top: 1%;
border: 2px blue dashed;
flex-flow: column wrap;
justify-content: center;
align-items: center;
min-width: 50%;

.button-wrapper {
    display: flex;
    justify-content: center;
    align-items: center;
    padding-top: 5%;
    padding-bottom: 5%;
}
`
