import React from 'react';
import {useState, useEffect} from 'react';
import {useForm} from 'react-hook-form';
import styled from 'styled-components';
import { Button, Form, Input, FormFeedback, FormGroup } from 'reactstrap'; 
import axios from 'axios';


export default function Login () {

    ////FORM STATE
    // const [initialFormValues, setInitialFormValues] = useState({
    //     email: '',
    //     password: '',
    //     }
    // )
    // const [formData, setFormData] = useState(initialFormValues);

    ////FORM VALIDATION
    //Extract the useForm hook methods
    const {
        register,
        errors,
        handleSubmit,
        reset,
        formState: { isSubmitSuccessful }
      } = useForm({ defaultValues: { something: "" } });

    const [submittedData, setSubmittedData] = useState({});

    const onSubmit = (data) => {
        setSubmittedData(data);
    };

    useEffect(() => {
        if (isSubmitSuccessful) {
        reset({ ...submittedData });
        }
    }, [isSubmitSuccessful, submittedData, reset]);

    console.log(submittedData);

    return (

        <StyledLogin>
            <form onSubmit={handleSubmit(onSubmit)}>

                    <input type="email" name="email" id="email" placeholder="Email" ref={register({required: true} )}/>
                    {errors.email && "Email is required"} 


                    <input type="password" name="password" id="password" placeholder="Password" ref={register({required: 'Please choose a password', minLength: {value: 8, message: 'Min length is 8 characters'} })}/>
                    {errors.password && <p> {errors.password.message} </p>} 


                    <button color='primary' type='submit'> Submit </button>  
            </form>          
        </StyledLogin>

    );
};

const StyledLogin = styled.div`
display: flex;
padding-top: 1%;
border: 2px blue dashed;
flex-flow: column wrap;
justify-content: center;
align-items: center;
min-width: 50%;

.button-wrapper {
    display: flex;
    justify-content: center;
    align-items: center;
    padding-top: 5%;
    padding-bottom: 5%;
}
`
