import React from 'react';
import styled from 'styled-components';
import {
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button
  } from 'reactstrap';


export default function Reviews (props) {
    const {reviews} = props;

    return (

        reviews.map((review) => {
            return(
            <StyledReviewDiv>
                 <CardBody>
                    <CardTitle tag="h5">{review.userName}</CardTitle>
                    <CardSubtitle tag="h6" className="mb-2 text-muted">{review.userName}</CardSubtitle>
                    <CardText>{review.reviewBody.slice(25)}</CardText>
                    <Button>View More</Button>
                    </CardBody> 
                <h2> 

                </h2>
                <br></br>
                <p>{review.reviewBody}</p>
            </StyledReviewDiv>
            )
        })
    );
};

