import React from 'react';

import styled from 'styled-components';
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap'; 
import {useForm} from 'react-hook-form';

export default function Signup () {

    //Create form state
    //Pass data from the form to the form state
    //Send the data through a post request
    //
    //Click handler to prevent default, call 

    ////FORM VALIDATION
    //Extract the useForm hook methods
    const { register, handleSubmit, errors } = useForm();

    //Event handler
    const onSubmit = (data, e) => {
        console.log(data);
        e.target.reset();
    };



    return (

        <StyledSignUp>
            <Form>
                <FormGroup>
                    <Label htmlFor="email" hidden>Email</Label>
                    <Input type="email" name="email" id="email" placeholder="Email" innerRef={register}/>
                </FormGroup>{' '}

                <FormGroup>
                    <Label htmlFor="name"></Label>
                    <Input type='name' name='name' id='name' placeholder='Enter your name' innerRef={register}/>
                    <FormText>First and Last Name</FormText>
                </FormGroup>

                <FormGroup>
                    <Label htmlFor="username"></Label>
                    <Input type='text' name='username' id='username' placeholder='Username' innerRef={register}/>
                    <FormText>Choose a handle that other users will see</FormText>
                </FormGroup>

                <FormGroup>
                    <Label htmlFor="password" hidden>Password</Label>
                    <Input type="password" name="password" id="password" placeholder="Password" innerRef={register}/>
                </FormGroup>{' '}

                <FormGroup check>
                    <Label check>
                    <Input type="checkbox" innerRef={register}/>{' '}
                    I accept terms and conditions
                    </Label>
                </FormGroup>

                <div className='button-wrapper'>
                <Button size="large" color='primary'>Sign Up</Button>
                </div>
            </Form>
        </StyledSignUp>

    );
};

const StyledSignUp = styled.div`
display: flex;
padding-top: 1%;
border: 2px blue dashed;
flex-flow: column wrap;
justify-content: space-evenly;
align-items: center;
min-width: 50%;

.button-wrapper {
    display: flex;
    justify-content: center;
    align-items: center;
    padding-top: 5%;
    padding-bottom: 5%;
}
`
